function installExtensions() { 
	var extensions = null;

  // extensions
  extensions = this.require('storage').get('system-designer-extensions');
  if (extensions && extensions.extensions && extensions.extensions.length) {
    extensions.extensions.forEach(function (extension) {
      var Extension = this.require('Extension'),
          system = {},
          cleanJson = {},
          reg = null;

      if (extension.installed) {    
        system = extension.source;

        try {
          cleanJson = JSON.stringify(system);
          reg = new RegExp(system._id, 'g');
          cleanJson = cleanJson.replace(reg, 'ext_' + system._id );
          system = JSON.parse(cleanJson);

          this.require('runtime').install(system);

          new Extension(extension);
        } catch (e) {
          this.require('message').danger('Can not install an extension.');
        }
      }
    }.bind(this));
  }
}

