function init(conf) { 
  var html = '',
      dom = null,
      system = this.require('factory').system();

  $('#designer-dialog').empty();

  html = this.require('dialog-modal-search.html');
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',
    html.source()
        .replace(/{{title}}/gi, this.title())
  );

  //events
  dom = document.getElementById('designer-dialog-search-modal-ok');
  dom.addEventListener('click', function click(event) {
    this.ok();
  }.bind(this));

  dom = document.getElementById('designer-dialog-search-result');
  dom.addEventListener('click', function click(event) {
    this.hide();
  }.bind(this));

  dom = document.getElementById('designer-dialog-input-search');
  dom.addEventListener('keyup', function keyup(event) {
    var currentSelection = 0;

    switch (true) {
      case event.keyCode === 37:
      case event.keyCode === 39:
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      case event.keyCode === 13:
        if (this.currentSelection() > -1) {
          document.getElementsByClassName('list-group-item active')[0].click();
        }
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      case event.keyCode === 38:
        currentSelection = this.currentSelection();
        if (currentSelection > 0) {
          $($('.list-group a')[currentSelection]).removeClass('active');
          currentSelection = currentSelection - 1;
          this.currentSelection(currentSelection);
          $($('.list-group a')[currentSelection]).addClass('active');
          $($('.list-group a')[currentSelection])[0].scrollIntoView();
        }
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      case event.keyCode === 40:
        currentSelection = this.currentSelection();
        if (currentSelection < this.nbResult() -1) {
          $($('.list-group a')[currentSelection]).removeClass('active');
          currentSelection = currentSelection + 1;
          this.currentSelection(currentSelection);
          $($('.list-group a')[currentSelection]).addClass('active');
          $($('.list-group a')[currentSelection])[0].scrollIntoView();
        }
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      default:
        this.filter(event.target.value);
        break;
    }
  }.bind(this));

  dom.addEventListener('keydown', function keydown(event) {
    if (event.keyCode === 13 || event.keyCode === 38 || event.keyCode === 40) {
      event.stopPropagation();
      event.preventDefault();
      return false;
    }
  }.bind(this));

  // focus
  $('#designer-dialog-search-modal').on('shown.bs.modal', function modal() {
    $('#designer-dialog-input-search').focus();
  });

  $('#designer-dialog-search-result-area').hide();
}

