function add() { 
	var Extension = this.require('Extension'),
	data = this.data(),
	system = {},
	extension = null,
	extensions = [],
	extensionToSave = {
	  extensions: []
	},
	cleanJson = '',
	reg = null,
	updated = false,
	fromExtension = false;

	if (data && Object.keys(data).length) {
  	if (data.isCore) {
  	  fromExtension = true;
  	  system = data.source();
  	} else {
  	  system = this.data();
  	}

  	if (system && Object.keys(system).length) {

  	  if (!fromExtension) {
    	  // update or add extension
      	extensions = this.require('db').collections().Extension.find();

        extensions.forEach(function forEach(extension) {
          if (extension.source._id === system._id) {
            extension.source = system;
            updated = true;
          }
        });

        if (!updated) {
        	new Extension({
        	  'source': system,
        	  'installed': true
        	});
        }
  	  } else {
  	    this.data().installed(true);
  	  }

      // save
      extensions = this.require('db').collections().Extension.find();

    	extensionToSave.extensions = extensions.map(function map(extension) {
    	  var clean = JSON.parse(JSON.stringify(extension));
    	  delete clean.classInfo;
    	  return clean;
    	});

      this.require('storage').set('system-designer-extensions', extensionToSave);

    	// install extension
    	// TODO put that in function
    	if (updated) {
    	  this.require('runtime').uninstall('ext_' + system._id)
    	}

      cleanJson = JSON.stringify(system);
      reg = new RegExp(system._id, 'g');
      cleanJson = cleanJson.replace(reg, 'ext_' + system._id);
      system = JSON.parse(cleanJson);

    	this.require('runtime').install(system);
    	this.require('menubar').render();
    	this.require('toolbar').render();

    	this.hide();
    	this.require('message').success('The extension has been added.');
  	}
	}
}

