function filter(value) { 
  var systemId = this.require('factory').system().id(),
    systemName = this.require('factory').system().name(),
    schemas = this.require('factory').system().schemas(),
    models = this.require('factory').system().models(),
    types = this.require('factory').system().types(),
    behaviors = this.require('factory').system().behaviors(),
    components = this.require('factory').system().components(),
    collection = '',
    id = '',
    toSearch = '',
    index = -1,
    result = [],
    nbItems = 0,
    nbResults = 0,
    numberResult = {};

  function showResult(str, index, value) {
    var result = '',
        length = value.length;

    result = str.substring(index - 20, index + length + 20);
    result = result.replace(/</g,'&lt;');
    result = result.replace(value, '<b>' + value + '</b>');

    return result;
  }  

  function showNumberResult(search, str) {
    var result = '',
        cleanSearch = '',
        reg = null,
        nb = null;

    cleanSearch = search.replace(/\(/g, '\\(');
    cleanSearch = cleanSearch.replace(/\)/g, '\\)');
    cleanSearch = cleanSearch.replace(/\[/g, '\\[');
    cleanSearch = cleanSearch.replace(/\]/g, '\\]');
    cleanSearch = cleanSearch.replace(/\./g, '\\.');
    cleanSearch = cleanSearch.replace(/\+/g, '\\+');
    cleanSearch = cleanSearch.replace(/\*/g, '\\*');
    cleanSearch = cleanSearch.replace(/\$/g, '\\\u0024');

    reg = new RegExp(cleanSearch, 'gi');
    nb = str.match(reg);

    if (nb && nb.length) {
      nbItems++;
      nbResults = nbResults + nb.length;
      result = '<span class="badge">' + nb.length + '</span>';
    }

    return {
      'nb': nb.length,
      'html': result
      };
  }

  function showInfoResult() {
    var info = '';

    if (nbResults > 1) {
      info = 'There are ' + nbResults + ' results';
    } else {
      info = 'There is '+ info + nbResults + ' result';
    }

    info = info + ' in ';

    if (nbItems > 1) {
      info = info + nbItems + ' elements';
    } else {
      info = info + nbItems + ' element';
    }

    if (nbResults > 0) {
      $('#designer-dialog-search-result-number').text(info);
    } else {
      $('#designer-dialog-search-result-number').text('');
    }
  }

  // clean
  document.getElementById('designer-dialog-search-result').innerHTML = '';

  if (value.length > 0 && value.trim() !== '') {

    // schema
    for (id in schemas) {
      toSearch = JSON.stringify(schemas[id]);
      index = toSearch.toLowerCase().indexOf(value.toLowerCase());
      if (index != -1) {
        numberResult = showNumberResult(value, toSearch);
        link = '<a href="#' + systemId + '#schemas#' + id + '" class="list-group-item" class="list-group-item active">' +
          '<h4 class="list-group-item-heading">Schema ' + schemas[id]._name + ' ' + numberResult.html + '</h4>' +
          '<p class="list-group-item-text">...' + showResult(toSearch, index, value) + '...</p>' +
          '</a>';

        result.push({
          'occurences': numberResult.nb,
          'html': link
        });
      }
    }

    // model
    for (id in models) {
      toSearch = JSON.stringify(models[id]);
      index = toSearch.toLowerCase().indexOf(value.toLowerCase());
      if (index != -1) {
        numberResult = showNumberResult(value, toSearch);
        link = '<a href="#' + systemId + '#models#' + id + '" class="list-group-item" class="list-group-item active">' +
          '<h4 class="list-group-item-heading">Model ' + models[id]._name + ' ' + numberResult.html + '</h4>' +
          '<p class="list-group-item-text">...' + showResult(toSearch, index, value) + '...</p>' +
          '</a>';

        result.push({
          'occurences': numberResult.nb,
          'html': link
        });
      }
    }

    // type
    for (id in types) {
      toSearch = JSON.stringify(types[id]);
      index = toSearch.toLowerCase().indexOf(value.toLowerCase());
      if (index != -1) {
        numberResult = showNumberResult(value, toSearch);
        link = '<a href="#' + systemId + '#types#' + types[id].name + '" class="list-group-item" class="list-group-item active">' +
          '<h4 class="list-group-item-heading">Type ' + types[id].name + ' ' + numberResult.html + '</h4>' +
          '<p class="list-group-item-text">...' + showResult(toSearch, index, value) + '...</p>' +
          '</a>';

        result.push({
          'occurences': numberResult.nb,
          'html': link
        });
      }
    }

    // behavior
    for (id in behaviors) {
      toSearch = JSON.stringify(behaviors[id]);
      index = toSearch.toLowerCase().indexOf(value.toLowerCase());
      if (index != -1) {
        numberResult = showNumberResult(value, toSearch);
        link = '<a href="#' + systemId + '#behaviors#' + behaviors[id].component + '#' + behaviors[id].state + '" class="list-group-item" class="list-group-item active">';

        if (behaviors[id].component === systemId) {
          link = link + '<h4 class="list-group-item-heading">Behavior ' + systemName + '.' + behaviors[id].state + ' ' + numberResult.html + '</h4>';
        } else { 
          link = link + '<h4 class="list-group-item-heading">Behavior ' + behaviors[id].component + '.' + behaviors[id].state + ' ' + numberResult.html + '</h4>';
        }

        link = link + '<p class="list-group-item-text">...' + showResult(behaviors[id].action, behaviors[id].action.toLowerCase().indexOf(value.toLowerCase()), value) + '...</p>' +
        '</a>';

        result.push({
          'occurences': numberResult.nb,
          'html': link
        });
      }
    }

    // components
    for (collection in components) {
      for (id in components[collection]) {
        toSearch = JSON.stringify(components[collection][id]);
        index = toSearch.toLowerCase().indexOf(value.toLowerCase());
        if (index != -1) {
          numberResult = showNumberResult(value, toSearch);
          link = '<a href="#' + systemId + '#components#' + collection + '#' + id + '" class="list-group-item" class="list-group-item active">' +
            '<h4 class="list-group-item-heading">Component ' + components[collection][id]._id + ' ' + numberResult.html + '</h4>' +
            '<p class="list-group-item-text">...' + showResult(toSearch, index, value) + '...</p>' +
            '</a>';

          result.push({
            'occurences': numberResult.nb,
            'html': link
          });
        }
      }
    }

    result.sort(function (resultA, resultB) {
      if (resultA.occurences < resultB.occurences) {
        return 1;
      }
      if (resultA.occurences > resultB.occurences) {
        return -1;
      }
      return 0;
    });

    showInfoResult();

    if (result.length === 0) {
      this.nbResult(0);
      result.push({
        'occurences': 0,
        'html': '<a class="list-group-item"><h4 class="list-group-item-heading">There is no result for your search</h4></a>'
      });
    } else {
      this.nbResult(result.length);
    }

    // result
    document.getElementById('designer-dialog-search-result').innerHTML = result.map(function(res) {
      return res.html;
    }).join(' ');

    if (this.nbResult() > 0) {
      $('.list-group a:first').addClass('active');
      this.currentSelection(0);
    } else {
      this.currentSelection(-1);
    }

    $('#designer-dialog-search-result-area').show();

    if (this.nbResult() > 0) {
      $('.list-group a:first')[0].scrollIntoView();
    }

  } else {
    $('#designer-dialog-search-result-area').hide();
  }
}

