function init(conf) { 
  var html = '',
      dom = null,
      system = this.require('factory').system();

  $('#designer-dialog').empty();

  html = this.require('dialog-modal-command.html');
  document.querySelector('#designer-dialog').insertAdjacentHTML('afterbegin',html.source());

  dom = document.getElementById('designer-dialog-input-command');
  dom.addEventListener('keyup', function keyup(event) {
    var currentSelection = 0;

    switch (true) {
      case event.keyCode === 37:
      case event.keyCode === 39:
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      case event.keyCode === 13:
        if (this.currentSelection() > -1) {
          $($('.list-group a')[this.currentSelection()]).click();
        }
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      case event.keyCode === 38:
        currentSelection = this.currentSelection();
        if (currentSelection > 0) {
          $($('.list-group a')[currentSelection]).removeClass('active');
          currentSelection = currentSelection - 1;
          this.currentSelection(currentSelection);
          $($('.list-group a')[currentSelection]).addClass('active');
          $($('.list-group a')[currentSelection])[0].scrollIntoView();
        }
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      case event.keyCode === 40:
        currentSelection = this.currentSelection();
        if (currentSelection < this.nbResult() -1) {
          $($('.list-group a')[currentSelection]).removeClass('active');
          currentSelection = currentSelection + 1;
          this.currentSelection(currentSelection);
          $($('.list-group a')[currentSelection]).addClass('active');
          $($('.list-group a')[currentSelection])[0].scrollIntoView();
        }
        event.stopPropagation();
        event.preventDefault();
        return false;
        break;
      default:
        this.filter(event.target.value);
        break;
    }
  }.bind(this));

  dom.addEventListener('keydown', function keydown(event) {
    if (event.keyCode === 13 || event.keyCode === 38 || event.keyCode === 40) {
      event.stopPropagation();
      event.preventDefault();
      return false;
    }
  }.bind(this));

  // focus
  $('#designer-dialog-command-modal').on('shown.bs.modal', function modal() {
    $('#designer-dialog-input-command').focus();
  });

  $('#designer-dialog-command-result-area').hide();
}

