function toJavaScript(logLevel) {
  var system = this.require('db').collections().System.find({
    '_id': this.require('factory').system().id()
  })[0];
  system = JSON.parse(JSON.stringify(system));
  delete system.classInfo;

  var source = this.require('javascript-export.js').source();

  source = source
    .replace(/{{version}}/g, this.require('factory').system().version())
    .replace(/{{description}}/g, this.require('factory').system().description().replace(/\n/g, '\n * '))
    .replace(/{{name}}/g, this.require('factory').system().name())
    .replace(/{{logLevel}}/g, $('#designer-dialog-export-options-log-level-select').val());

  // SYSTEM

  var confSystem = {};

  confSystem._id = system._id;
  confSystem.name = system.name;
  confSystem.version = system.version;
  confSystem.description = system.description;

  source = source + '/**\n';
  source = source + ' * System ' + confSystem.name + '\n';
  source = source + ' * @system\n';
  source = source + ' */\n';
  source = source + 'runtime.system(' + JSON.stringify(confSystem, null, '\t', 2) + ');\n\n';

  // SCHEMA / MODEL / TYPE     
  if (Object.keys(system.schemas).length > 0 || Object.keys(system.types).length > 0) {
    source = source + '\n// Schemas, models and types creation\n\n\n';
  }

  var name = '';
  var schemaSorted = [];
  var schemaNameSorted = [];
  var schemaName = {};

  for (name in system.schemas) {
    schemaSorted.push(system.schemas[name]);
    schemaNameSorted.push(system.schemas[name]._name);
    schemaName[system.schemas[name]._name] = system.schemas[name]._name;
  }
  schemaSorted.sort(function (schemaA, schemaB) {
    return schemaA._name > schemaB._name;
  });
  schemaNameSorted.sort();

  schemaSorted.forEach(function (schema) {
    source = source + '/**\n';
    source = source + ' * Schema ' + schema._name + '\n';
    source = source + ' * @schema\n';
    source = source + ' */\n';
    source = source + 'runtime.require(\'metamodel\').schema(' + JSON.stringify(schema, null, '\t', 2) + ');\n\n';
  });

  var modelSorted = [];
  var modelNameSorted = [];
  for (name in system.models) {
    modelSorted.push(system.models[name]);
    modelNameSorted.push(system.models[name]._name);
  }
  modelSorted.sort(function (modelA, modelB) {
    return modelA._name > modelB._name;
  });
  modelNameSorted.sort();

  modelSorted.forEach(function (model) {
    source = source + '/**\n';
    source = source + ' * Model ' + model._name + '\n';
    source = source + ' * @model\n';
    source = source + ' */\n';
    source = source + 'runtime.require(\'metamodel\').model(' + JSON.stringify(model, null, '\t', 2) + ');\n\n';
  });

  var typeSorted = [];
  var typeNameSorted = [];
  for (name in system.types) {
    typeSorted.push(system.types[name]);
    typeNameSorted.push(system.types[name].name);
  }
  typeSorted.sort(function (typeA, typeB) {
    return typeA.name > typeB.name;
  });
  typeNameSorted.sort();

  typeSorted.forEach(function (type) {
    source = source + '/**\n';
    source = source + ' * Type ' + type.name + '\n';
    source = source + ' * @type\n';
    source = source + ' */\n';
    source = source + 'runtime.require(\'metamodel\').type(' + JSON.stringify(type, null, '\t', 2) + ');\n\n';
  });

  if (Object.keys(system.schemas).length > 0 || Object.keys(system.models).length > 0 || Object.keys(system.types).length > 0) {
    source = source + 'runtime.require(\'metamodel\').create();\n\n';
  }

  // COMPONENT   
  if (Object.keys(system.components).length > 0) {
    source = source + '\n// Components creation\n\n\n';
  }
  schemaNameSorted.forEach(function (collection) {
    for (name in system.components[collection]) {
      source = source + '/**\n';
      source = source + ' * Component ' + system.components[collection][name]._id + '\n';
      source = source + ' * @component\n';
      source = source + ' */\n';
      source = source + 'new (runtime.require(\'' + collection + '\'))(' + JSON.stringify(system.components[collection][name], null, '\t', 2) + ');\n\n';
    }
  });

  // BEHAVIOR   
  if (Object.keys(system.behaviors).length > 0) {
    source = source + '\n// Behaviors creation\n\n\n';
  }

  var behaviors = {};
  var behaviorsComponentNameSorted = [];
  var modelsByName = {};

  for (name in system.behaviors) {
    if (typeof behaviors[system.behaviors[name].component] === 'undefined') {
      behaviors[system.behaviors[name].component] = [];
    }
    behaviors[system.behaviors[name].component].push(system.behaviors[name]);
  }

  behaviorsComponentNameSorted = Object.keys(behaviors);
  behaviorsComponentNameSorted.sort();

  var id = '';
  for (id in system.models) {
    modelsByName[system.models[id]._name] = system.models[id];
  }

  behaviorsComponentNameSorted.forEach(function forEach(componentName) {
    var componentBehaviors = behaviors[componentName];

    componentBehaviors.sort(function sort(behaviorA, behaviorB) {
      return behaviorA.state > behaviorB.state;
    });

    if (componentBehaviors.length > 0) {
      if (componentName === system._id) {
        source = source + '// system behaviors\n\n';
      } else {
        source = source + '// ' + componentName + ' behaviors\n\n';
      }
    }

    componentBehaviors.forEach(function forEach(behavior) {
      var useCoreAPI = typeof behavior.useCoreAPI !== 'undefined' ? behavior.useCoreAPI : false;
      var core = typeof behavior.core !== 'undefined' ? behavior.core : false;
      var comp = '';
      var behaviorParamDef = {};
      var options = ');\n\n';

      if (useCoreAPI && !core) {
        options = ', true);\n\n';
      }
      if (useCoreAPI && core) {
        options = ', true, true);\n\n';
      }

      source = source + '/**\n';
      source = source + ' * @method ' + behavior.state + '\n';

      if (modelsByName[behavior.component] && modelsByName[behavior.component][behavior.state] && modelsByName[behavior.component][behavior.state].params) {
        modelsByName[behavior.component][behavior.state].params.forEach(function forEach(param) {
          source = source + ' * @param {' + param.type + '} ' + param.name + '\n';
        });
      }
      if (modelsByName[behavior.component] && modelsByName[behavior.component][behavior.state] && modelsByName[behavior.component][behavior.state].result) {
        source = source + ' * @returns {' + modelsByName[behavior.component][behavior.state].result + '} \n';
      }

      source = source + ' */\n';

      switch (true) {
        case behavior.component === system._id:
          comp = 'runtime.system()';
          break;
        default:
          comp = 'runtime.require(\'' + behavior.component + '\')';
          break;
      }

      source = source + comp + '.on(\'' + behavior.state + '\', ' + behavior.action + options;
    })
  });

  // START
  source = source + '// Start the system\n';
  source = source + 'runtime.system().start();\n';

  return source;
}

