function refresh() {
  var config = {},
    gh = null,
    userName = '',
    repository = '';

  config = this.require('storage').get('system-designer-config');
  if (config && config.githubToken && config.githubRepository) {

    gh = new GitHub({
      token: atob(config.githubToken)
    });

    userName = config.githubRepository.split('/')[0];
    repository = config.githubRepository.split('/')[1];

    repo = gh.getRepo(userName, repository);

    repo.getContents('master', '', 'raw')
      .then(function (contents) {
        contents.data.forEach(function (item) {
          if (item.type === 'file' && item.name.indexOf('.json') !== -1) {
            repo.getContents('master', item.name, 'raw')
              .then(function (content) {
                var system = content.data,
                  systems = runtime.require('storage').get('system-designer-systems'),
                  sys = null,
                  sysStorage = null;

                // check valid system
                if (
                  (Object.keys(system).indexOf('schemas') !== -1) &&
                  (Object.keys(system).indexOf('models') !== -1) &&
                  (Object.keys(system).indexOf('behaviors') !== -1) &&
                  (Object.keys(system).indexOf('types') !== -1) &&
                  (Object.keys(system).indexOf('components') !== -1)
                ) {

                  delete system.classInfo;

                  if (runtime.require('storage').get(system._id)) {
                    runtime.require('github').merge(system);;
                  } else {
                    runtime.require('storage').set(system._id, system);
                  }

                  // save index
                  if (!systems) {
                    systems = { 'systems': [system._id] };
                  } else {
                    if (systems.systems.indexOf(system._id) === -1) {
                      systems.systems.push(system._id);
                    }
                  }
                  runtime.require('storage').set('system-designer-systems', systems);

                  // refresh
                  if (!runtime.require('factory').system()) {
                    sys = runtime.require('factory').create(system);
                    runtime.require('state').space(sys.name());
                  }

                  runtime.require('spaces').render();
                  runtime.require('workspace').render();
                  runtime.require('router').update();

                  runtime.require('designer').save();
                }
              })
              .catch(function (e) {
                runtime.require('message').danger('<strong>GitHub:</strong> ' + e.response.data.message + '.');
              });
          }
        });

        if (contents.data.length === 0) {
          runtime.require('message').info('there is no system in your repository.');
        } else {
          runtime.require('message').success('Pull request finished with success.');
        }
      })
      .catch(function (e) {
        runtime.require('message').danger('<strong>GitHub:</strong> ' + e.response.data.message + '.');
      });
  } else {
    runtime.require('message').warning('No configuration found for GitHub. Please update the configuration.');
  }
}

