function mergeSystem(sys) { 
  var System = this.require('System'),
    name = '',
    propName = '',
    modelName = '',
    compId = '',
    modelId = '',
    schemaId = '',
    factory = this.require('factory'),
    schemas = {},
    models = {},
    types = {},
    components = {},
    behaviors = {},
    system = this.system(),
    message = this.require('message');

  function _getModelId(name) {
    var result = '',
      id = '';

    for (id in factory.system().models()) {
      if (factory.system().models()[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _getSchemaId(name) {
    var result = '',
      id = '';

    for (id in factory.system().schemas()) {
      if (factory.system().schemas()[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _canOverrideBehavior(id, component, state, behaviors) {
    var behavior = {},
      behaviorId = '';

    for (behaviorId in behaviors) {
      behavior = behaviors[behaviorId];
      if (behavior.component === component && behavior.state === state && behavior._id !== id) {
        delete behaviors[behavior._id];
        break;
      }
    }
  }

  function _canOverride(sysId, schemas, behaviors, behavior) {
    var def = {},
      id = '',
      result = true;

    id = _getSchemaId(behavior.component);
    def = schemas[id];

    if (def && def[behavior.state]) {
      if (def[behavior.state] === 'method') {
        _canOverrideBehavior(behavior._id, behavior.component, behavior.state, behaviors);
      }
    }

    if ((behavior.state === 'main' || behavior.state === 'start' || behavior.state === 'stop') && behavior.component === sysId) {
      result = false;
    }

    return result;
  }

  if (Object.keys(sys).length) {

    // schemas
    schemas = JSON.parse(JSON.stringify(this.require('factory').system().schemas()));
    for (name in sys.schemas) {
      if (schemas[name]) {
        for (propName in sys.schemas[name]) {
          schemas[name][propName] = sys.schemas[name][propName];
        }
      } else {
        if (!_getSchemaId(sys.schemas[name]._name)) {
          schemas[name] = sys.schemas[name];
        } else {
          for (propName in sys.schemas[name]) {
            if (propName.indexOf('_') !== 0) {
              schemas[_getSchemaId(sys.schemas[name]._name)][propName] = sys.schemas[name][propName];
            }
          }
        }
      }
    }
    this.require('factory').system().schemas(schemas);

    // behaviors
    behaviors = JSON.parse(JSON.stringify(this.require('factory').system().behaviors()));
    for (name in sys.behaviors) {
      if (name !== sys._id && _canOverride(sys._id, schemas, behaviors, sys.behaviors[name])) {
        behaviors[name] = sys.behaviors[name];
      } else {
        // merge main / start / stop
        if ((sys.behaviors[name].state === 'main' || sys.behaviors[name].state === 'start' || sys.behaviors[name].state === 'stop') && sys.behaviors[name].component === sys._id) {
          this.require('factory').mergeBehavior(behaviors, sys.behaviors[name], this.require('factory').system().id(), sys.name);
        }
      }
    }
    this.require('factory').system().behaviors(behaviors);


    // models
    models = JSON.parse(JSON.stringify(this.require('factory').system().models()));
    for (name in sys.models) {
      if (models[name]) {
        for (propName in sys.models[name]) {
          models[name][propName] = sys.models[name][propName];
        }
      } else {
        if (!_getModelId(sys.models[name]._name)) {
          models[name] = sys.models[name];
        } else {
          for (propName in sys.models[name]) {
            if (propName.indexOf('_') !== 0) {
              models[_getModelId(sys.models[name]._name)][propName] = sys.models[name][propName];
            }
          }
        }
      }
    }

    // sync models
    for (schemaId in schemas) {
      this.require('factory').syncModel(schemas[schemaId]);
    }

    this.require('factory').system().models(models);

    // sync behaviors and components
    for (modelId in models) {
      this.require('factory').syncBehavior(models[modelId]);
    }

    // types
    types = JSON.parse(JSON.stringify(this.require('factory').system().types()));
    for (name in sys.types) {
      if (types[name]) {
        for (propName in sys.types[name]) {
          types[name][propName] = sys.types[name][propName];
        }
      } else {
        types[name] = sys.types[name];
      }
    }
    this.require('factory').system().types(types);

    // components
    components = JSON.parse(JSON.stringify(this.require('factory').system().components()));
    for (modelName in sys.components) {
      if (!components[modelName]) {
        components[modelName] = {};
      }
      for (compId in sys.components[modelName]) {
        if (components[modelName][compId]) {
          for (propName in sys.components[modelName][compId]) {
            components[modelName][compId][propName] = sys.components[modelName][compId][propName];
          }
        } else {
          components[modelName][compId] = sys.components[modelName][compId];
        }
      }
    }
    this.require('factory').system().components(components);

    // sync components
    for (modelId in models) {
      this.require('factory').syncComponent(models[modelId]);
    }

    this.require('factory').system().schemas(schemas);
    this.require('factory').system().models(models);
    this.require('factory').system().types(types);
    this.require('factory').system().behaviors(behaviors);
    this.require('factory').system().components(components);

    if (this.require('factory').system().id() === sys._id) {
      this.require('factory').system().version(sys.version);
      this.require('factory').system().description(sys.description);
    }

    message.success('Composition of the system is done.');
  } else {
    if (this.require('factory').system()) {
      message.danger('The system you try to import is invalid.');
    } else {
      message.danger('There is no system to compose with.');
    }
  }
}

