function create(sys, withStartMethod) { 
  var System = this.require('System'),
      uuid = this.generateId(),
      mainUuid = this.generateId(),
      system = {},
      instance = null,
      migrateSys = '';

  sys = JSON.parse(JSON.stringify(sys));    

  // deprecated property
  delete sys.subsystem;

  // migrate v1->v2
  migrateSys = JSON.stringify(sys);
  migrateSys = migrateSys.replace(/\"RuntimeComponent\"/g, '"_Component"');
  migrateSys = migrateSys.replace(/\"RuntimeDatabase\"/g, '"_Database"');
  migrateSys = migrateSys.replace(/\"RuntimeChannel\"/g, '"_Channel"');
  // migrateSys = migrateSys.replace(/\"\u0040/g, '"');

  sys = JSON.parse(migrateSys);


  // default system
  system = {
    "name": "default",
    "master": true,
    "version": "0.0.1",
    "description": "",
    "schemas": {},
    "models": {},
    "behaviors": {},
    "types": {},
    "components": {},
    "_id": uuid
  };

  for (var prop in sys) {
    if (sys.hasOwnProperty(prop)) {
      system[prop] = sys[prop];
    }
  }

  if (withStartMethod) {
    // add main method
    system.behaviors[mainUuid] = {
      "_id": mainUuid,
      "component": system._id,
      "state": "start",
      "action": this.require('language').createStartBehavior(),
      "useCoreAPI": false,
      "core": false
    };
  }

  // add (TODO improve)
  if (this.system()) {
    this.system().destroy();
  }

  instance = new System(system);

  this.system(instance);

  return instance;
}

