  function _getSchemaId(name) {
    var result = '',
      id = '';

    for (id in that.system().schemas()) {
      if (that.system().schemas()[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _getModelId(name) {
    var result = '',
      id = '';

    for (id in that.system().models()) {
      if (that.system().models()[id]._name === name) {
        result = id;
        break;
      }
    }
    return result;
  }

  function _existBehavior(state, space, model) {
    var result = false;

    if (_isModel(space)) {
      for (id in that.system().behaviors()) {
        if (that.system().behaviors()[id].state === state && that.system().behaviors()[id].component === model) {
          result = true;
          break;
        }
      }
    } else {
      for (id in that.system().behaviors()) {
        if (that.system().behaviors()[id].state === state && that.system().behaviors()[id].component === space) {
