        }
      }

      // case of structure
      if (state.indexOf('.') !== -1 && schemas[schemaId][state.split('.')[0]] === 'property' && typeof types[models[modelId][state.split('.')[0]].type] !== 'undefined') {
        if (types[models[modelId][state.split('.')[0]].type].schema[state.split('.')[1]].type === 'array') {
          params = language.createBehaviorParametersEventArray();
        } else {
          params = language.createBehaviorParametersEvent();
        }
      }

      // collection params
      if (schemas[schemaId][state] === 'collection') {
        params = language.createBehaviorParametersEventArray();
      }

      // method params
      if (schemas[schemaId][state] === 'method') {
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      // _Component state params
      if (state === 'init') {
        params = 'conf';
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      if (state === 'destroy') {
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      if (state === 'error') {
        params = 'data';
        if (_existBehavior(state, this.require('state').space(), model)) {
          canCreate = false;
        }
      }

      // body
      if (modelDef[state]) {
        result = modelDef[state].result;
      }
      if (result) {
       body = this.require('language').createBehaviorBody(result);
      }
    } else {
      component = this.require('factory').system().id();
      if (_existBehavior(state, component, model)) {
        canCreate = false;
      }
    }

    if (canCreate) {
      // set model
      if (state !== 'destroy') {
        behavior = {
          "_id": uuid,
          "component": component,
          "state": state,
          "action": this.require('language').createBehavior(state, params, body),
          "useCoreAPI": false,
          "core": false
        };
      } else {
        behavior = {
          "_id": uuid,
          "component": component,
          "state": "destroy",
          "action": this.require('language').createDestroyBehavior(),
          "useCoreAPI": true,
          "core": false
        };
      }

      behaviors[uuid] = behavior;
      this.require('factory').system().behaviors(behaviors);
    }
  }

  return JSON.parse(JSON.stringify(behavior));
}

