function render() { 
  var domItems = document.getElementById('designer-toolbar-items'),
    i = 0,
    length = 0,
    item = null,
    toolBarItems = [],
    platformToolBarItems = [],
    platform = '',
    self = this;

  // empty
  $('#designer-toolbar-items').empty();
  this.items([]);

  // get items
  toolBarItems = this.require('db').collections().ToolBarItem.find({
    'type': this.require('state').type(),
    'platform': 'all'
  });

  // get platform specific items
  switch (true) {
    case this.require('designer').isElectron() === true:
      platform = 'electron';
      break;
    case this.require('designer').isCordova() === true:
      platform = 'cordova';
      break;
    default:
      platform = 'web';
      break;
  }

  platformToolBarItems = this.require('db').collections().ToolBarItem.find({
    'type': this.require('state').type(),
    'platform': platform
  });

  toolBarItems = toolBarItems.concat(platformToolBarItems);

  // sort items
  toolBarItems.sort(function (itemA, itemB) {
    if (itemA.position > itemB.position) {
      return 1;
    }
    if (itemA.position < itemB.position) {
      return -1;
    }
    return 0;
  });

  toolBarItems.forEach(function (toolBarItem) {
    var id = toolBarItem._id;
    self.items().push(self.require(id));
  });

  // render items
  this.items().forEach(function (item) {
    domItems.insertAdjacentHTML('beforeend', '<li>' + item.html().source() + '</>');
  });

  // add events
  length = domItems.children.length;
  for (i = 0; i < length; i++) {
    item = domItems.children[i];
    item.addEventListener('click', function () {
      this.click();
    }.bind(self.items(i)));
  }
}

