s1bc6b11e9a13afd.action:
function save() { 
  var val = this.require('editor').getValue(),
    designer = this.require('designer'),
    message = this.require('message'),
    model = null,
    property = '',
    modelGen = null;

  try {
    model = JSON.parse(val);
  } catch (e) {
    message.danger('Can not save your model: your model has an invalid structure.');
    return;
  }

  if (!model._name) {
    message.danger('The property \'_name\' is missing.');
    return;
  }

  if (!model._id) {
    message.danger('The property \'_id\' is missing.');
    return;
  }

  for (property in model) {
    if (property.indexOf(' ') !== -1) {
      message.danger('Invalid property name \'' + property + '\. <br>Space is not authorized in the name of a property.');
      return;
    }
    propVal = model[property];
    if (typeof propVal === 'object' && !Array.isArray(propVal) && property.indexOf('_') === 0) {
      message.danger('Invalid property name \'' + property + '\. <br>A property name can not start with \'_\'.');
      return;
    }
  }

  modelGen = this.require('store').extra();
  for (property in model) {
    if (property.indexOf('_') !== 0 && typeof modelGen[property] === 'undefined') {
      message.danger('Invalid property name \'' + property + '\. <br>\'' + property + '\ must be defined in the schema.');
      return;
    }
  }

  if (this.require('store').data()._name === model._name) {
    this.require('store').data(model);

    // check if ID change
    if (this.require('store').uuid() !== this.require('store').data()._id) {
      this.require('channel').$editorUpdateModelId(this.require('store').uuid(), this.require('store').data()._id);
      this.require('store').uuid(this.require('store').data()._id);
    }

    this.require('channel').$editorUpdateModel(this.require('store').uuid(), this.require('store').data());
    message.success('Model saved.');
  } else {
    message.danger('You can not modify the name of a model.');
  }
}

