function start() { 
  var currentPath = window.location.href.split('#/')[1];

  // init 
  if (currentPath) {
    this.path(currentPath);
  }

  // on url change
  window.addEventListener('hashchange', function (e) {
    this.path(window.location.href.split('#/')[1]);
  }.bind(this));
}

