function get(id) { 
  var store = null,
      todos = {
        'Todo': {}
      },
      result = [];

	try { 
    if (typeof localStorage !== 'undefined') {
      store = localStorage.getItem('todos-system');
      if (store) {
        todos = JSON.parse(store);
      }

      result = todos.Todo[id];
    } else {
    this.require('logger').warn('no localStorage object found.');
    }
  } catch(e) {}

  return result;
}

