function render() {
  var model = this.model(),
      query = this.require('db').collections()[model].find({}),
      props = this.require(model).classInfo().properties(),
      links = this.require(model).classInfo().links(),
      html = '<table class="table">',
      i = 0,
      j = 0;

  // header
  html = html + '<tr>';
  for (i=1; i<props.length; i++) {
    html = html + '<th scope="col">' + props[i] +'</th>'; 
  }
  for (i=0; i<links.length; i++) {
    html = html + '<th scope="col">' + links[i] +'</th>';   
  }
  html = html + '<tr>';

  // body
  for (i=0; i<query.length; i++) {
    html = html + '<tr>';
    for (j=1; j<props.length; j++) {
      html = html + '<td>' + query[i][props[j]] + '</td>';
    }
    for (j=0; j<links.length; j++) {
      if (typeof query[i][links[j]] === 'string' && this.require(query[i][links[j]])) {
        html = html + '<td>' + this.require(query[i][links[j]]).fullName() + '</td>';
      } else {
        html = html + '<td></td>';
      }
    }
    html = html + '</tr>';
  }

  // end table
  html = html + '</table>';

  // body
  if (document.getElementById('table')) {
    document.getElementById('table').innerHTML = html;
  }
}

