18fba183c51300f.action:
function render(elt) { 
  var html = '',
      domNode = elt,
      listeners = {},
      domId = '',
      inc = 0,
      self = this,
      activeElementId = '',
      activeElement = null,
      cursorPosition = 0;

  if (domNode) {
    activeElementId = document.activeElement.id;
    activeElement = document.getElementById(activeElementId);
    if (activeElement) {
      cursorPosition = activeElement.selectionStart;
    }
    domNode.innerHTML = '';
  }    

  if (this.template()) {
    html = this.template().source();
  }    

  // render html
  if (html) {

    // search for input value with component property
    html = html.replace(
      /value="\{[^\}]+\}"/g,
      function (match){
        var id = self.id() + (inc + 1),
            prop = match.replace('value="{','').replace('}"','').trim();

        listeners[id] = prop;    
        return ' id="' + id +'" ' + match;
      }
    );

    // set value  
    this.classInfo().properties().forEach(function (val) {
      var reg = new RegExp('\{' + val + '\}', 'g');
      html = html.replace(reg, this[val]());
    }.bind(this));

    if (domNode === null) {
      domNode = document.body;
    }

    domNode.insertAdjacentHTML('beforeend', html);

    // add listeners
    for (domId in listeners) {
      document.getElementById(domId).addEventListener('keyup', function keyup(e) {
        self[listeners[domId]](this.value);  
      });
      document.getElementById(domId).addEventListener('input', function input(e) {
        self[listeners[domId]](this.value);  
      });
      document.getElementById(domId).addEventListener('blur', function blur(e) {
        self[listeners[domId]](this.value);  
      });
      document.getElementById(domId).addEventListener('change', function change(e) {
        self[listeners[domId]](this.value);  
      });
    }

    // restore focus if any
    activeElement = document.getElementById(activeElementId);
    if (activeElement) {
      activeElement.focus();
      activeElement.value = activeElement.value;
      activeElement.setSelectionRange(cursorPosition, cursorPosition);
    }
  }
}

1e11612b8915c5d.action:
function load(dom) { 
	var components = Object.keys(this.require('db').collections()),
	    tagNames = {},
	    doms = document.getElementsByTagName('*'),
	    i = 0,
	    length = doms.length,
	    domElt = null,
	    attributes = [],
	    j = 0,
	    nbAtt = 0,
	    config = {},
	    Component = null,
	    component = null;

	  // list all the component classes
    components.forEach(function (val) {
      tagNames[val.toUpperCase()] = val;
    });

    // check if a HTML tag if a Web Component
    for (i = 0; i < length; i++) {
        domElt = doms[i];
        if (tagNames[domElt.tagName]) {
              attributes =  domElt.attributes;

              nbAtt = attributes.length;
              for (j = 0; j < nbAtt; j++) {
                if (attributes[j].name === 'id') {
                  config._id = attributes[j].value; 
                } else {
                  config[attributes[j].name] = attributes[j].value; 
                }
              }

              Component = this.require(tagNames[domElt.tagName]);
              component = new Component(config);
              domElt.setAttribute('id', component.id());
              component.render(domElt);
        }
    }
}

