function start() {
  var http = global.require('http');

  // Create server
  http.createServer(function (req, res) {
    var RequestHandler = this.require('RequestHandler'),
        StreamHandler = this.require('StreamHandler'),
        PageRequestHandler = this.require('PageRequestHandler'),
        requestHandler = null,
        streamHandler = null,
        pageRequestHandler = null,
        page = null;

    switch (req.method) {
      case 'GET':
        switch (true) {
          case this.pushEnabled() && req.url === '/stream':
            streamHandler = new StreamHandler({
              request: req,
              response: res
            });
            streamHandler.stream();
            break;
          case req.url.indexOf('.html') !== -1:
            pageRequestHandler = new PageRequestHandler({
              request: req,
              response: res
            });
            pageRequestHandler.sendPage(req.url);
            break;
          case req.url.indexOf('.html') === -1 && req.url !== '/stream':
            requestHandler = new RequestHandler({
              request: req,
              response: res
            });
            requestHandler.get();
            break;
          default:
            break;
        }
        break;
      case 'DELETE':
        requestHandler = new RequestHandler({
          request: req,
          response: res
        });
        requestHandler.del();
        break;
      case 'POST':
        requestHandler = new RequestHandler({
          request: req,
          response: res
        });
        requestHandler.post();
        break;
      case 'PUT':
        requestHandler = new RequestHandler({
          request: req,
          response: res
        });
        requestHandler.put();
        break;
      case 'PATCH':
        requestHandler = new RequestHandler({
          request: req,
          response: res
        });
        requestHandler.patch();
        break;
      default:
        break;
    }
  }.bind(this)).listen(this.require('restServer').port(), this.require('restServer').ip());

  this.require('logger').info('the server is started on http://' + this.require('restServer').ip() + ':' + this.require('restServer').port());
  this.require('logger').info('REST apis are active on ' + this.require('restServer').apiName());
}

