function load() { 
  var fs = global.require('fs'),
      logger = this.require('logger'),
      collection = '',
      id = '';

  fs.readFile(global.process.env.PWD + '/datastore.json', 'UTF-8', function (err, dataStore) {
    if (err) {
      fs.writeFile(global.process.env.PWD + '/datastore.json', '{}', 'UTF-8', function (err) {
        if (err) {
          logger.error(err);
          return;
        }
        this.status('loaded');
        this.save();
      }.bind(this));
      return;
    }
    if (dataStore.trim() !== '') {
      dataStore = JSON.parse(dataStore);

      // insert all documents in System Runtime database
      for (collection in dataStore) {
        for (id in dataStore[collection]) {
          $db[collection].insert(dataStore[collection][id]);
        }
      }
    }
    this.status('loaded');
  }.bind(this));
}

