function save() { 
	var collections = this.require('db').collections(),
	    docs = [],
	    store = {},
	    collection = '',
	    fs = global.require('fs'),
	    logger = this.require('logger');

	for (collection in collections) {
	  if ($metamodel.inheritFrom(collection, 'SyncComponent') && collection !== 'SyncComponent') {
	    store[collection] = {};

	    docs = collections[collection].find();
	    docs.forEach(function forEach(doc) {
	      var storedoc = JSON.parse(JSON.stringify(doc));

	      delete storedoc.classInfo;
	      store[collection][storedoc._id] = storedoc;
	    });
	  }
	}    

  if (this.status() === 'loaded') {
    fs.writeFile(global.process.env.PWD + '/datastore.json', JSON.stringify(store), 'UTF-8', function (err) {
      if (err) {
        logger.error(err);
        return;
      }
    });    
  }
}

