function insert(event) { 
  var logs = [],
      lastInsertOrder = -1;

  if ($metamodel.inheritFrom(event.collection,'SyncComponent')) {    
    if (this.require('restServer').fileStorageEnabled()) {
      this.require('fileStorage').save();
    }

    if (this.require('db').collections().StreamHandler.count() > 0) {
      // search if last insert action before this one 
      // was a message from the server or not
      logs = $db._Log.find({
        'action': 'insert',
        'id': event.document._id
      });

      if (logs.length) {
        logs.sort(function sort(logA, logB) {
          return logA.order - logB.order;
        });
        lastInsertOrder = logs[0].order;
      }

      logs = $db._Log.find({
        'order': lastInsertOrder - 1,
        'collection': '_Message'
      });

      switch (true) {
        case !logs[0]:
        case logs[0] && 
             logs[0].value.event !== 'clientDatabaseInsert':
        case logs[0] && logs[0].value.event === 'clientDatabaseInsert' && 
             logs[0].value.data[0].document._id !== event.document._id:

            // an event is send to the channel
            this.require('channel').serverDatabaseInsert(event);
          break;
        default:
          break;
      }
    }
  }
}

