      lastInsertOrder = -1;

  if ($metamodel.inheritFrom(event.collection,'SyncComponent')) {
    if (this.require('restServer').fileStorageEnabled()) {
      this.require('fileStorage').save();
    }

    if (this.require('db').collections().StreamHandler.count() > 0) {
