      logs = $db._Log.find({
        'action': 'update',
        'id': event.id
      });

      if (logs.length) {
        logs.sort(function sort(logA, logB) {
          return logA.order - logB.order;
        });
        lastInsertOrder = logs[0].order;
      }

      logs = $db._Log.find({
        'order': lastInsertOrder - 1,
        'collection': '_Message'
      });

      switch (true) {
        case !logs[0]:
        case logs[0] && 
