function externalGet(event, requestHandlerId) { 
  var search = [],
      result = '',
      requestHandler = null;

  // find document(s)
  if (event.collection && $db[event.collection]) {
    if (event.id) {
      search = $db[event.collection].find({
        '_id': event.id
      });

      if (search.length) {
        search = search[0];
        if (event.property) { // get a component property
          search = JSON.parse(JSON.stringify(search));
          result = search[event.property];
        } else { // get a component
          search = JSON.parse(JSON.stringify(search));
          delete search.classInfo;
          result = search;
        }
      } else {
        result = '';
        this.require('logger').warn('There is no document with _id ' + '\'' + event.id + '\ (for the collection \'' + event.collection + '\')');
      }
    } else { // get a collection
      result = [];

      search = $db[event.collection].find();
      search.forEach(function (val) {
        var doc = val;

        doc = JSON.parse(JSON.stringify(doc));
        delete doc.classInfo;

        result.push(doc);
      });
    }
  }

  // response  

  requestHandler = this.require(requestHandlerId);
  requestHandler.response().writeHead(200, { 
    'Content-Type': 'application/json', 
    'Access-Control-Allow-Origin': '*' 
  });

  if (result === '') {
    requestHandler.response().end('');
  } else {
    requestHandler.response().end(JSON.stringify(result));
  }	
}

