function createFilter(criteria) { 
	var result = [],
	    model = '',
      props = [];

	if (this.require('list').model() && criteria !== '') {
    model = this.require('list').model(),
    props = this.require(model).classInfo().properties(); 

    props.forEach(function (val) {
      var crit = {};
      if (val !== 'classInfo') {
        crit[val]  = new RegExp(criteria);
        result.push(crit);
      }
    });
	}
	return result;
}

